// JavaScript Document
$(document).on('click', 'ul.logo',function(){
    "use strict";
    if ($('body').hasClass('empty')) {
        //nothing
    }
    else {
        $('body').addClass('empty');
        $('body section.content').html('');
    }
});

$(document).on('click', '[data-action]',function(){
    "use strict";
    var action = $(this).attr('data-action'),
        $tr = $(this).closest('tr');
    switch (action) {
        case 'delete':
            swal({
              title: "Триене на склад",
              text: "Сигурни ли сте, че искате да изтриете склад " + $tr.attr('data-target'),
              type: "info",
              showCancelButton: true,
              closeOnConfirm: false,
              showLoaderOnConfirm: true
            }, function () {
              setTimeout(function () {
                //swal("Складът беше успешно изтрит!");
                  swal("Складът беше изтрит!", null, "success");
                  $tr.remove();
              }, 2000);
            });
        break;
    }
});